import React, { useState } from 'react';
import ButtonClass from './ButtonClass';
import ButtonFunction from './ButtonFunction';

const App = () => {
  const [data, setData] = useState('');
  const [savedDataList, setSavedDataList] = useState([]);
  const [displaySavedData, setDisplaySavedData] = useState(false);

  const saveToLocalStorage = () => {
    if (data.trim() !== '') {
      setDisplaySavedData(false); 
      savedDataList.push(data);
      localStorage.setItem('data', JSON.stringify(savedDataList));
      setSavedDataList(savedDataList);
      setData('');
    }
  };

  const retrieveFromLocalStorage = () => {
    const savedData = localStorage.getItem('data');
    setSavedDataList(savedData ? JSON.parse(savedData) : []);
    setDisplaySavedData(true);
  };

  const handleChange = (event) => {
    setData(event.target.value);
  };

  return (
    <div>
      <h1>Local Storage App</h1>
      <input type="text" value={data} onChange={handleChange} />
      <ButtonFunction label="Save (Function Component)" onClick={saveToLocalStorage} />
      <ButtonClass label="Retrieve (Class Component)" onClick={retrieveFromLocalStorage} />
      {displaySavedData && (
        <>
          <p>Saved Data:</p>
          <ul>
            {savedDataList.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default App;
