import React, { useState, useEffect } from 'react';
import ButtonClass from './ButtonClass';
import ButtonFunction from './ButtonFunction';

const App = () => {
  const [data, setData] = useState('');
  const [savedDataList, setSavedDataList] = useState([]);
  const [displaySavedData, setDisplaySavedData] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    retrieveFromLocalStorage();
  }, []);

  const saveToLocalStorage = () => {
    if (data.trim() !== '') {
      if (editMode) {
        // Edit the existing row
        const updatedDataList = [...savedDataList];
        updatedDataList[editIndex] = data;
        localStorage.setItem('data', JSON.stringify(updatedDataList));
        setSavedDataList(updatedDataList);
        setData('');
        setEditMode(false);
        setEditIndex(null);
      } else {
        // Add a new row
        const updatedDataList = [...savedDataList, data];
        localStorage.setItem('data', JSON.stringify(updatedDataList));
        setSavedDataList(updatedDataList);
        setData('');
      }
    }
  };

  const retrieveFromLocalStorage = () => {
    const savedData = localStorage.getItem('data');
    setSavedDataList(savedData ? JSON.parse(savedData) : []);
    setDisplaySavedData(true);
  };

  const handleEdit = (index) => {
    setEditMode(true);
    setEditIndex(index);
    setData(savedDataList[index]);
  };

  const handleRemove = (index) => {
    const updatedDataList = savedDataList.filter((item, i) => i !== index);
    localStorage.setItem('data', JSON.stringify(updatedDataList));
    setSavedDataList(updatedDataList);
  };

  const handleChange = (event) => {
    setData(event.target.value);
  };

  return (
    <div>
      <h1>Local Storage Table App</h1>
      <form onSubmit={saveToLocalStorage}>
        <input type="text" value={data} onChange={handleChange} />
        <ButtonFunction label={editMode ? 'Save Edit' : 'Add Row'} onClick={saveToLocalStorage} />
        {displaySavedData && (
          <table>
            <thead>
              <tr>
                <th>Row Data</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {savedDataList.map((item, index) => (
                <tr key={index}>
                  <td>{item}</td>
                  <td>
                    <ButtonClass label="Edit" onClick={() => handleEdit(index)} />
                    <ButtonFunction label="Remove" onClick={() => handleRemove(index)} />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </form>
    </div>
  );
};

export default App;
