import React, { useState, useEffect } from 'react';
import ButtonClass from './ButtonClass';
import ButtonFunction from './ButtonFunction';

const App = () => {
  const [data, setData] = useState('');
  const [savedDataList, setSavedDataList] = useState([]);
  const [displaySavedData, setDisplaySavedData] = useState(false);

  // Add state for table rows
  const [tableData, setTableData] = useState([]);
  const [currentRow, setCurrentRow] = useState({ id: null, data: '' });

  // Load data from localStorage on initial render
  useEffect(() => {
    const savedTableData = localStorage.getItem('tableData');
    if (savedTableData) {
      setTableData(JSON.parse(savedTableData));
    }
  }, []);

  // Save tableData to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('tableData', JSON.stringify(tableData));
  }, [tableData]);

  const retrieveFromLocalStorage = () => {
    const savedData = localStorage.getItem('data');
    setSavedDataList(savedData ? JSON.parse(savedData) : []);
    setDisplaySavedData(true);
  };

  const handleAddRow = () => {
    if (data.trim() !== '') {
      setTableData([...tableData, { id: Date.now(), data }]);
      setData('');
    }
  };

  const handleEditRow = (id) => {
    const rowToEdit = tableData.find((row) => row.id === id);
    if (rowToEdit) {
      setData(rowToEdit.data);
      setCurrentRow({ id, data: rowToEdit.data });
    }
  };

  const handleUpdateRow = () => {
    if (data.trim() !== '') {
      setTableData((prevTableData) =>
        prevTableData.map((row) =>
          row.id === currentRow.id ? { ...row, data } : row
        )
      );
      setData('');
      setCurrentRow({ id: null, data: '' });
    }
  };

  const handleRemoveRow = (id) => {
    setTableData(tableData.filter((row) => row.id !== id));
  };

  const handleChange = (event) => {
    setData(event.target.value);
  };

  return (
    <div>
      <h1>Local Storage App</h1>
      <input type="text" value={data} onChange={handleChange} />
      {/* Use appropriate buttons to add or update row */}
      {currentRow.id === null ? (
        <ButtonFunction
          label="Add Row"
          onClick={handleAddRow}
        />
      ) : (
        <ButtonFunction
          label="Update Row"
          onClick={handleUpdateRow}
        />
      )}
      <ButtonClass label="Retrieve (Class Component)" onClick={retrieveFromLocalStorage} />
      {displaySavedData && (
        <>
          <p>Saved Data:</p>
          <ul>
            {savedDataList.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </>
      )}

      {/* Table to display and manipulate rows */}
      <table>
        <thead>
          <tr>
            <th>Index</th>
            <th>Row Data</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((row, index) => (
            <tr key={row.id}>
              <td>{index}</td> {/* Display the index of the row */}
              <td>{row.data}</td>
              <td>
                <ButtonFunction
                  label="Edit"
                  onClick={() => handleEditRow(row.id)}
                />
                <ButtonFunction
                  label="Remove"
                  onClick={() => handleRemoveRow(row.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
