import React, { useState, useEffect } from 'react';
import ButtonClass from './ButtonClass';
import ButtonFunction from './ButtonFunction';

const App = () => {
  const [data, setData] = useState('');
  const [savedDataList, setSavedDataList] = useState([]);
  const [displaySavedData, setDisplaySavedData] = useState(false);

  // Add state for table rows
  const [tableData, setTableData] = useState([]);
  const [currentRow, setCurrentRow] = useState({ id: null, data: '' });

  const [updateIndex, setUpdateIndex] = useState(null);
  const [updateName, setUpdateName] = useState('');

  // Load data from localStorage on initial render
  useEffect(() => {
    const savedTableData = localStorage.getItem('tableData');
    if (savedTableData) {
      setTableData(JSON.parse(savedTableData));
    }
  }, []);

  // Save tableData to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('tableData', JSON.stringify(tableData));
  }, [tableData]);


  const handleAddRow = () => {
    if (data.trim() !== '') {
      setTableData([...tableData, { id: Date.now(), data }]);
      setData('');
    }
  };

  const handleEditRow = (id) => {
    const rowToEdit = tableData.find((row) => row.id === id);
    if (rowToEdit) {
      setData(rowToEdit.data);
      setCurrentRow({ id, data: rowToEdit.data });
      setUpdateIndex(tableData.findIndex((row) => row.id === id));
      setUpdateName(rowToEdit.data);
    }
  };

  const handleUpdateRow = () => {
    if (data.trim() !== '') {
      setTableData((prevTableData) =>
        prevTableData.map((row) =>
          row.id === currentRow.id ? { ...row, data } : row
        )
      );
      setData('');
      setCurrentRow({ id: null, data: '' });
      setUpdateIndex(null);
      setUpdateName('');
    }
  };

  const handleRemoveRow = (id) => {
    setTableData(tableData.filter((row) => row.id !== id));
  };

  const handleChange = (event) => {
    setData(event.target.value);
  };

  const handleUpdateFormSubmit = (event) => {
    event.preventDefault();
    if (updateIndex !== null && updateName.trim() !== '') {
      setTableData((prevTableData) =>
        prevTableData.map((row, index) =>
          index === updateIndex ? { ...row, data: updateName } : row
        )
      );
      setUpdateIndex(null);
      setUpdateName('');
    }
  };

  const handleUpdateIndexChange = (event) => {
    const newIndex = parseInt(event.target.value, 10);
    setUpdateIndex(isNaN(newIndex) ? null : newIndex);
  };

  const handleUpdateNameChange = (event) => {
    setUpdateName(event.target.value);
  };

  return (
    <div>
      <h1>Local Storage App</h1>
      <input type="text" value={data} onChange={handleChange} />
      {/* Use appropriate buttons to add or update row */}
      {currentRow.id === null ? (
        <ButtonFunction
          label="Add Row"
          onClick={handleAddRow}
        />
      ) : (
        <ButtonFunction
          label="Update Row"
          onClick={handleUpdateRow}
        />
      )}


      {/* Form for updating rows */}
      {currentRow.id !== null && (
        <form onSubmit={handleUpdateFormSubmit}>
          <label>
            Update Index:
            <input
              type="number"
              value={updateIndex !== null ? updateIndex : ''}
              onChange={handleUpdateIndexChange}
            />
          </label>
          <label>
            Update Name:
            <input
              type="text"
              value={updateName}
              onChange={handleUpdateNameChange}
            />
          </label>
          <ButtonFunction label="Update Row" type="submit" />
          <ButtonFunction label="Cancel" onClick={() => setCurrentRow({ id: null, data: '' })} />
        </form>
      )}

      {/* Table to display and manipulate rows */}
      <table>
        <thead>
          <tr>
            <th>Index</th>
            <th>Row Data</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((row, index) => (
            <tr key={row.id}>
              <td>{index}</td> 
              <td>{row.data}</td>
              <td>
                <ButtonFunction
                  label="Edit"
                  onClick={() => handleEditRow(row.id)}
                />
                <ButtonClass
                  label="Remove"
                  onClick={() => handleRemoveRow(row.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
